import streamlit as stimport yfinance as yfst.title('hello word')st.text('insira seu nome')nome= st.text_input('nome')st.write(f'olá {nome}')def inverter_texto(texto):	return texto [: : -1]nome_invertido=inverter_texto(nome)st.write(f'Seu nome invertido é {nome_invertido}')# perguntar ao usuario o nome da ação, data inicial e data final e plotar o gráfico do preço da ação no do diast.text('insira o nome da ação')acao= st.text_input('nome da ação') + '.SA'st.text('insira a data inicial')data_inicial= st.date_input('data inicial')st.text('insira a data final')data_final= st.date_input('data final')b1=st.button('buscar')if b1:	acao= yf.download(acao, start=data_inicial, end=data_final)	st.line_chart(acao['Close'])	st.write(acao)